package a1_출력;

public class Hellojava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 주석
		System.out.println("Hello, Java!");
		// System : 운영체제의 기능을 사용
		// out : 운영체제의 기능 중 출력을 담당하는 기능
		// print : 출력을 담당하는 기능 중 입력하는 만큼만 출력(줄바꿈 X)
		// println : 출력을 담당하는 기능 중 한줄을 출력(줄바꿈 자동)
		// (;)세미콜론 : 명령의 마지막 지점을 나타냄.
		
		System.out.println("Hello, Java2!");
		
		
		//     << 단축키 >>
		// 실행 단축키 : Ctrl + F11
		// 문장 자동완성 : Ctrl + Space
		// 문장 복사 : Ctrl + Alt + 위/아래 방향키
		// import가 필요한 전부를 자동으로 import : Ctrl + Shift + o

		// 흐름도 작성 : https://app.diagrams.net/
	
	}

}





